// Импорт библиотек
import { useState, useEffect, useContext } from 'react';
import styled from 'styled-components';

// Импорт роутеров
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';

//Импорт стилей
import GlobalStyle from './StyledComponentsApp/GlobalStyle.js';
import DefaultBackground from './StyledComponentsApp/EpicBackgroundStyle.js';

//Импорт React компонентов
//import Header from './components/Header';
import HomePage from './components/HomePage';
import Dashboard from './components/Dashboard';
import ResetPassword from './components/ResetPassword';
import ConsoleGame from './components/ConsoleGame';

// Импорт провайдеров
import { UserProvider, UserContext } from './contexts/UserContext.js';
import { ScreenProvider } from './contexts/ScreenContext.js';

// Импорт сервисов
import { checkToken } from './services/authService.js';

// Импорт картинок
import logo from './img/logo.png';


const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  min-width: 300px;
  font-family: 'Qanelas', sans-serif;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow-x: hidden;

  @media (min-width: 300px) {
    overflow-x: auto;
  }
`;

const AppContentContainer = styled.div`
  width: 99vw;
  height: 99vh;
`;

const EpicHeaderContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

const EpicLogotype = styled.img`
  height: auto;
  width: 70px;

  @media (min-width: 576px) {
    width: 100px;
  }

  @media (min-width: 768px) {
    width: 150px;
  }

  @media (min-width: 992px) {
    width: 200px;
  }

  @media (min-width: 1200px) {
    width: 250px;
  }

  @media (min-width: 1400px) {
    width: 300px;
  }
`;

const EpicTitle = styled.h1`
  width: 100%;
  text-align: center;
  align-items: center;
  color: #FFD700;
  font-size: 18px;
  font-weight: 600;
  margin-top: 20px;

  @media (min-width: 576px) {
    font-size: 28px;
  }

  @media (min-width: 768px) {
    font-size: 38px;
  }

  @media (min-width: 992px) {
    font-size: 52px;
  }

  @media (min-width: 1200px) {
    font-size: 62px;
  }

  @media (min-width: 1400px) {
    font-size: 72px;
  }

  @media (min-width:1500px) {
    font-size: 72px;
  }

  @media (min-width: 1600px) {
    font-size: 82px;
  }
`;

const EpicHeaderButtonsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: calc(90% - 100px);
  padding: 5px;
  height: 99%;
`;

const EpicButton = styled.button`
  color: #FFD700;
  border: 2px solid #707070;
  background-color: transparent;
  border-radius: 8px;
  padding: 8px 32px;
  margin: 5px 0;
  font-size: 14px;
  line-height: 20px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background-color: #FFD700;
    color: #000;
  }

  &:active {
    background-color: #e6be00;
    border-color: #505050;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(255, 215, 0, 0.5);
  }
`;

const enterFullScreen = () => {
    if (document.documentElement.requestFullscreen) {
        document.documentElement.requestFullscreen();
    } else if (document.documentElement.mozRequestFullScreen) { // Firefox
        document.documentElement.mozRequestFullScreen();
    } else if (document.documentElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
        document.documentElement.webkitRequestFullscreen();
    } else if (document.documentElement.msRequestFullscreen) { // IE/Edge
        document.documentElement.msRequestFullscreen();
    }
};

const Header = () => {
    const { user, logout, isAuthenticated } = useContext(UserContext);

    return (
        <header>
            <EpicHeaderContainer>
                <>
                    <a href="/">
                        <EpicLogotype src={logo} alt="Логотип" />
                    </a>
                    {(!user || !isAuthenticated) ? (
                        <EpicHeaderButtonsContainer>
                            <EpicButton registration>Регистрация</EpicButton>
                            <EpicButton>Войти</EpicButton>
                            <EpicButton onClick={enterFullScreen}>Развернуть на весь экран</EpicButton>
                        </EpicHeaderButtonsContainer>
                    ) : (
                        <div>test</div>
                    )}
                </>
            </EpicHeaderContainer>
        </header>
    )
}

const AppContent = ({ isAuthenticated }) => {
    const location = useLocation();
    const showHeader = !['/console-game/:id_game'].some((path) =>
        location.pathname.startsWith(path.split(':')[0])
    );

    return (
        <DefaultBackground>
            <AppContainer>
                <AppContentContainer>
                    {showHeader && <Header />}
                    <EpicTitle>Эпические настольные онлайн игры</EpicTitle>
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <HomePage />} />
                        <Route path="/reset-password/:token" element={<ResetPassword />} />
                        <Route path="/console-game/:id_game" element={<ConsoleGame />} />
                    </Routes>
                </AppContentContainer>
            </AppContainer>
        </DefaultBackground>
    );
};

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const verifyToken = async () => {
            const isAuth = await checkToken();
            setIsAuthenticated(isAuth);
        };

        verifyToken();

        const intervalId = setInterval(verifyToken, 60000);

        return () => clearInterval(intervalId);
    }, []);

    return (
        <Router>
            <UserProvider>
                <ScreenProvider>
                    <GlobalStyle />
                    <AppContent isAuthenticated={isAuthenticated} />
                </ScreenProvider>
            </UserProvider>
        </Router>
    );
};

export default App;
