// Импорт библиотек
import React, { useState, useEffect, useContext } from 'react';
import styled from 'styled-components';

// Импорт роутеров
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';

//Импорт стилей
import GlobalStyle from './StyledComponentsApp/GlobalStyle.js';
import DefaultBackground from './StyledComponentsApp/EpicBackgroundStyle.js';
import { darkThemeEpicGames } from './StyledComponentsApp/themes.js'

//Импорт React компонентов
import Header from './components/Header';
import HomePage from './components/HomePage';
import Dashboard from './components/Dashboard';
import ResetPassword from './components/ResetPassword';
import ConsoleGame from './components/ConsoleGame';

// Импорт провайдеров
import { UserProvider, UserContext } from './contexts/UserContext.js';
import { ScreenProvider } from './contexts/ScreenContext.js';
import { ThemeProvider } from 'styled-components';

// Импорт сервисов
import { checkToken } from './services/authService.js';

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  min-width: 300px;
  font-family: 'Qanelas', sans-serif;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow-x: hidden;

  @media (min-width: 300px) {
    overflow-x: auto;
  }
`;

const AppContentContainer = styled.div`
  width: 99vw;
  height: 99vh;
`;

const EpicTitle = styled.h1`
  width: 100%;
  text-align: center;
  align-items: center;
  color: #FFD700;
  font-size: 18px;
  font-weight: 600;
  margin-top: 20px;

  @media (min-width: 576px) {
    font-size: 28px;
  }

  @media (min-width: 768px) {
    font-size: 38px;
  }

  @media (min-width: 992px) {
    font-size: 52px;
  }

  @media (min-width: 1200px) {
    font-size: 62px;
  }

  @media (min-width: 1400px) {
    font-size: 72px;
  }

  @media (min-width:1500px) {
    font-size: 72px;
  }

  @media (min-width: 1600px) {
    font-size: 82px;
  }
`;

const AppContent = ({ isAuthenticated }) => {
    const location = useLocation();
    const showHeader = !['/console-game/:id_game'].some((path) =>
        location.pathname.startsWith(path.split(':')[0])
    );

    return (
        <DefaultBackground>
            <AppContainer>
                <AppContentContainer>
                    {showHeader && <Header />}
                    <EpicTitle>Эпические настольные онлайн игры</EpicTitle>
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <HomePage />} />
                        <Route path="/reset-password/:token" element={<ResetPassword />} />
                        <Route path="/console-game/:id_game" element={<ConsoleGame />} />
                    </Routes>
                </AppContentContainer>
            </AppContainer>
        </DefaultBackground>
    );
};

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [theme, setTheme] = useState(darkThemeEpicGames);

    useEffect(() => {
        const verifyToken = async () => {
            const isAuth = await checkToken();
            setIsAuthenticated(isAuth);
        };

        verifyToken();

        const intervalId = setInterval(verifyToken, 60000);

        return () => clearInterval(intervalId);
    }, []);

    return (
        <Router>
            <UserProvider>
                <ScreenProvider>
                    <ThemeProvider theme={theme}>
                        <GlobalStyle />
                        <AppContent />
                    </ThemeProvider>
                </ScreenProvider>
            </UserProvider>
        </Router>
    );
};

export default App;
