// GamePartsConstructor.js
import './GamePartsConstructor.css'

import React, { useState, useEffect } from 'react';
import axios from 'axios';

import svgEdit from '../../assets/icons/edit.svg';
import svgDelete from '../../assets/icons/delete.svg';
import svgNewDocument from '../../assets/icons/new_document.svg';

const GamePartsConstructor = ({ game }) => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/${game.id}/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, [game]);

    const handleAddPart = () => {
        // Логика добавления части
    };

    const handleEditPart = (partId) => {
        // Логика редактирования части
    };

    const handleDeletePart = (partId) => {
        // Логика удаления части
    };

    return (
        <div>
            <h2 className='title-constructor'>Конструктор частей/глав</h2>
            {error && <p className="error">{error}</p>}
            <button className='button-new-document' onClick={handleAddPart}>Создать <img src={svgNewDocument} alt='Добавить' /></button>
            <div className='constructor-window-container'>
                <ul className='elements-list-from-server'>
                    {parts.map(part => (
                        <li className='li-element-lists' key={part.id}>
                            {part.name}
                            <div className='button-group-container'>
                                <button className='button-edit-document' onClick={() => handleEditPart(part)}><img src={svgEdit} alt='Редактировать' /></button>
                                <button className='button-delete-document' onClick={() => handleDeletePart(part)}><img src={svgDelete} alt='Удалить' /></button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default GamePartsConstructor;
