import React from 'react';
import Users from './Users';
import Games from './Games';

const ContentWindow = ({ selectedComponent }) => {
    return (
        <div className="content-window p-4 bg-white rounded shadow-sm">
            {selectedComponent === 'users' && <Users />}
            {selectedComponent === 'games' && <Games />}
            {selectedComponent === 'settings' && <div>Настройки пользователя</div>}
            {/* Добавьте отображение других компонентов по мере необходимости */}
        </div>
    );
};

export default ContentWindow;
