import './PartForm.css';

import React, { useState } from 'react';

const PartForm = ({ initialData, onSave, onCancel }) => {
    const [name, setName] = useState(initialData ? initialData.name : '');
    const gameName = initialData ? initialData.game.name : 'Название игры';
    const authorLogin = initialData ? initialData.author.login : 'Логин автора';

    const handleSubmit = () => {
        const partData = {
            name,
            game: { name: gameName }
        };
        onSave(partData);
    };

    const handleClose = () => {
        onCancel();
    };

    return (
        <div className='part-form-container'>
            <div className='form-container'>
                <div className='button-header-container'>
                    <button className='close-button' onClick={handleClose}>×</button>
                </div>
                <h2 className='title-form'>{ initialData ? 'Редактирование главы/части' : 'Создание главы/части'}</h2>
                <div className='form-group'>
                    <label className='form-label'>Наименование:</label>
                    <input className='form-input' type='text' value={name} onChange={(e) => setName(e.target.value)} />
                </div>
                <div className='form-group'>
                    <label className='form-label'>Игра:</label>
                    <input className='form-input' type='text' value={gameName} readOnly />
                </div>
                <div className='form-group author'>
                    <label className='form-label'>Автор:</label>
                    <span className='author-value'>{authorLogin}</span>
                </div>
                <div className='button-footer-container'>
                    <button className='form-button' onClick={handleSubmit}>Сохранить</button>
                    <button className='form-button' onClick={onCancel}>Отменить</button>
                </div>
            </div>
        </div>
    );
};

export default PartForm;
