import React from 'react';

const Register = ({ setActiveComponent }) => {
    return (
        <div>
            <h2>Зарегистрироваться</h2>
            <input type="text" placeholder="Логин" />
            <input type="password" placeholder="Пароль" />
            <input type="password" placeholder="Повторить пароль" />
            <input type="email" placeholder="e-mail" />
            <button>Зарегистрироваться</button>
            <button onClick={() => setActiveComponent('login')}>У меня уже есть аккаунт</button>
            <a href="#" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</a>
        </div>
    );
};

export default Register;

