import React from "react";
import styled from "styled-components";
import EpicGamesLogotype from './EpicGamesLogotype';
import { useScreen } from '../../contexts/ScreenContext';

const Container = styled.div`
    display: flex;
    flex-direction: ${({ $orientation }) => $orientation === "landscape" ? "row" : "column"};
    width: 100vw;
    height: 100vh;
    background-color: #15171E;
`;

const FirstSection = styled.div`
    width: ${({ $orientation }) => $orientation === "landscape" ? "80vw" : "100vw"};
    height: ${({ $orientation }) => $orientation === "landscape" ? "100vh" : "80vh"};
`;

const SecondSection = styled.div`
    width: ${({ $orientation }) => $orientation === "landscape" ? "20vw" : "100vw"};
    height: ${({ $orientation }) => $orientation === "landscape" ? "100vh" : "20vh"};
`;

const PlatformApp = () => {
    const { orientation } = useScreen();

    return (
        <Container $orientation={orientation}>
            <FirstSection $orientation={orientation}>
                <EpicGamesLogotype isLink={true} hasText={false} />
            </FirstSection>
            <SecondSection $orientation={orientation}>
                {/* Ваш контент для второй секции */}
            </SecondSection>
        </Container>
    );
}

export default PlatformApp;
