// ScreenContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';

export const ScreenContext = createContext();

export const useScreen = () => {
    return useContext(ScreenContext);
};

export const ScreenProvider = ({ children }) => {
  const [orientation, setOrientation] = useState(window.innerWidth > window.innerHeight ? 'landscape' : 'portrait');
  const [resolution, setResolution] = useState({ width: window.innerWidth, height: window.innerHeight });
  const [screenSize, setScreenSize] = useState(null);

  useEffect(() => {
    const handleResize = () => {
      setOrientation(window.innerWidth > window.innerHeight ? 'landscape' : 'portrait');
      setResolution({ width: window.innerWidth, height: window.innerHeight });

      if (resolution.width >= 576 && resolution.width < 768) {
        setScreenSize('eSmall');
      } else if (resolution.width >= 768 && resolution.width < 992) {
        setScreenSize('small');
      } else if (resolution.width >= 992 && resolution.width < 1200) {
        setScreenSize('normal');
      } else if (resolution.width >= 1200 && resolution.width < 1400) {
        setScreenSize('large');
      } else if (resolution.width >= 1400) {
        setScreenSize('eLarge');
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <ScreenContext.Provider value={{ orientation, resolution, screenSize }}>
      {children}
    </ScreenContext.Provider>
  );
};
