import React, { useState } from 'react';

const PartForm = ({ initialData, onSave, onCancel }) => {
    const [name, setName] = useState(initialData ? initialData.name : '');
    const gameName = initialData ? initialData.game.name : 'Название игры';
    const authorLogin = initialData ? initialData.author.login : 'Логин автора';
    const authorEmail = initialData ? initialData.author.email : 'Email автора';

    const handleSubmit = () => {
        const partData = {
            name,
            game: { name: gameName },
            author: { login: authorLogin, email: authorEmail }
        };
        onSave(partData);
    };

    return (
        <div className='form-container'>
            <div>
                <label>Наименование:</label>
                <input type='text' value={name} onChange={(e) => setName(e.target.value)} />
            </div>
            <div>
                <label>Игра:</label>
                <input type='text' value={gameName} readOnly />
            </div>
            <div>
                <label>Автор:</label>
                <div><span>{authorLogin}</span> <span>{authorEmail}</span></div>
            </div>
            <button onClick={handleSubmit}>Сохранить</button>
            <button onClick={onCancel}>Отменить</button>
        </div>
    );
};

export default PartForm;
