import React, { useState, useContext } from 'react';
import Modal from './Modal';
import { UserContext } from '../contexts/UserContext';
import { ScreenContext } from '../contexts/ScreenContext';
import logo from '../logo.png'

const User = () => {
    const { user } = useContext(UserContext);

    const styleUserContainer = {

    }

    return (
        <div className='profile card p-3 mb-3 d-flex align-items-center'>
            <div className='user-display-name text-center'>
                {user ? <p className="h5">{user.displayName}</p> : <p className="text-muted">Информация о пользователе не доступна.</p>}
            </div>
            <div className='user-avatar mt-2'>
                {user ? (
                    <img className='avatar-image rounded-circle' src={`../img/avatars/${user.avatarFilename}`} alt="User Avatar" width="50" />
                ) : (
                    <p className="text-muted">Аватарка не доступна.</p>
                )}
            </div>
        </div>
    );
};

const Header = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { user, logout, isAuthenticated } = useContext(UserContext);
    const { orientation, resolution } = useContext(ScreenContext);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    if (orientation === 'landscape') {
        return (
            <header style={styleHeader}>
                <a href="/">
                    <img src={logo} alt="Логотип" style={{ maxWidth: '100px', height: 'auto' }} />
                </a>
                {(!user || !isAuthenticated) ? (
                    <button onClick={openModal}>Войти</button>
                ) : (
                    <button onClick={logout}>Выйти</button>
                )}
            </header>
        );
    } else {
        return (
            <header>
                
            </header>
        );
    }

    //return (
    //    <div></div>
        /*<header className="d-flex justify-content-between align-items-center p-3 bg-transparent" style={{ width: '80%', maxWidth: '1900px', margin: '0 auto' }}>
            <a href="/">
                <img src={logo} className="img-fluid" alt="Логотип" style={{ maxWidth: '100px', height: 'auto' }} />
            </a>
            {(!user || !isAuthenticated) ? (
                <button className="btn btn-outline-primary" onClick={openModal}>Войти</button>
            ) : (
                <div>
                    <User />
                    <button className="btn btn-outline-danger" onClick={logout}>Выйти</button>
                </div>
            )}
            {isModalOpen && <Modal closeModal={closeModal} />}
        </header>*/
    //);
};

export default Header;
