import './ConstructorGame.css';

import React, { useState } from 'react';

// Конструкторы игры
import GamePartsConstructor from './GamePartsConstructor';
import GameLocationsConstructor from './GameLocationsConstructor';
import GameWizardsConstructor from './GameWizardsConstructor';
import GameWizardsDecksConstructor from './GameWizardsDecksConstructor';
import GameDarkArtsConstructor from './GameDarkArtsConstructor';
import GameVillainsConstructor from './GameVillainsConstructor';
import GameHogwartsDeckConstructor from './GameHogwartsDeckConstructor';

const ConstructorGames = ({ game }) => {
    const [hoveredButton, setHoveredButton] = useState(null);
    const [selectedConstructor, setSelectedConstructor] = useState(null);

    const buttonLabels = {
        parts: 'Конструктор части/главы',
        locations: 'Конструктор локаций',
        wizards: 'Конструктор волшебников',
        villains: 'Конструктор злодеев',
        darkArts: 'Конструктор темных искусств',
        wizardsDecks: 'Конструктор стартовых колод волшебников',
        hogwartsDeck: 'Конструктор колоды Хогвартса'
    };

    return (
        <div className='constructor-game-container'>
            <div className='constructor-list-container'>
                <h2 className='constructor-list-title'>Конструкторы</h2>
                {Object.keys(buttonLabels).map((key) => (
                    <button
                        key={key}
                        style={{
                            ...constructorButton,
                            ...(hoveredButton === key ? constructorButtonHover : {})
                        }}
                        onMouseEnter={() => setHoveredButton(key)}
                        onMouseLeave={() => setHoveredButton(null)}
                        onClick={() => setSelectedConstructor(key)}
                    >
                        {buttonLabels[key]}
                    </button>
                ))}
            </div>
            <div style={constructorWindowContainer}>
                {selectedConstructor === 'parts' && <GamePartsConstructor game={game} />}
                {selectedConstructor === 'locations' && <GameLocationsConstructor />}
                {selectedConstructor === 'wizards' && <GameWizardsConstructor />}
                {selectedConstructor === 'villains' && <GameVillainsConstructor />}
                {selectedConstructor === 'darkArts' && <GameDarkArtsConstructor />}
                {selectedConstructor === 'wizardsDecks' && <GameWizardsDecksConstructor />}
                {selectedConstructor === 'hogwartsDeck' && <GameHogwartsDeckConstructor />}
            </div>
        </div>
    );
};

export default ConstructorGames;

const constructorButton = {
    color: '#FFFFFF',
    fontWeight: 600,
    backgroundColor: 'transparent',
    border: 'none',
    cursor: 'pointer',
    padding: '5px',
    textAlign: 'left'
};

const constructorButtonHover = {
    textDecoration: 'underline'
};

const constructorWindowContainer = {
    width: '60vw',
    height: 'auto',
    margin: '10px',
    padding: '10px',
};
