import './Register.css';
import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [repeatPassword, setRepeatPassword] = useState('');
    const [displayName, setDisplayName] = useState('');
    const [errors, setErrors] = useState({});

    const validateEmail = (email) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return re.test(String(email).toLowerCase());
    };

    const validatePassword = (password) => {
        const re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/;
        return re.test(password);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let validationErrors = {};

        if (!validateEmail(email)) {
            validationErrors.email = 'Некорректный e-mail';
        }
        if (!validatePassword(password)) {
            validationErrors.password = 'Пароль должен содержать минимум 8 символов, включая хотя бы одну заглавную букву, одну строчную букву и одну цифру. Разрешены специальные символы: @$!%*?&^#-_';
        }
        if (password !== repeatPassword) {
            validationErrors.repeatPassword = 'Пароли не совпадают';
        }

        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}api/users/register`, { email, password, repeatPassword, displayName });
            if (response.data.msg) {
                alert(`${response.data.msg}`);
            } else {
                alert(`${response.data.errors}`);
            }
        } catch (error) {
            alert('Ошибка регистрации пользователя!');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>E-mail:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                {errors.email && <p className="error">{errors.email}</p>}
            </div>
            <div>
                <label>Пароль:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                {errors.password && <p className="error">{errors.password}</p>}
            </div>
            <div>
                <label>Повтор пароля:</label>
                <input type="password" value={repeatPassword} onChange={(e) => setRepeatPassword(e.target.value)} required />
                {errors.repeatPassword && <p className="error">{errors.repeatPassword}</p>}
            </div>
            <div>
                <label>Отображаемое имя:</label>
                <input type="text" value={displayName} onChange={(e) => setDisplayName(e.target.value)} required />
            </div>
            <button type="submit">Регистрация</button>
        </form>
    );
};

export default Register;
