import styled from 'styled-components';

const StyledSectionSubtitle = styled.p`
  font-size: 1.25rem;
  color: ${props => props.theme.textSecondary};
  margin-bottom: 2rem;
  max-width: 2xl;
  margin-left: auto;
  margin-right: auto;
`;

export default StyledSectionSubtitle;
