// GameComponent.js
import React, { useState, useEffect } from 'react';

const GameComponent = () => {
    const [title, setTitle] = useState('');
    const [activeTab, setActiveTab] = useState('constructor');

    useEffect(() => {
        // Здесь вы можете сделать запрос к серверу для получения заголовка
        // Пример установки заголовка после получения данных
        setTitle('Заголовок из БД');
    }, []);

    const renderContent = () => {
        switch (activeTab) {
            case 'constructor':
                return <div>Вкладка конструктора игрового процесса</div>;
            case 'create':
                return <div>Настройки для создания новой игры</div>;
            case 'connect':
                return <div>Список игр для подключения</div>;
            default:
                return null;
        }
    };

    return (
        <div>
            <h1>{title}</h1>
            <div>
                <button onClick={() => setActiveTab('constructor')}>Конструктор</button>
                <button onClick={() => setActiveTab('create')}>Создать игру</button>
                <button onClick={() => setActiveTab('connect')}>Подключиться к игре</button>
            </div>
            <div>
                {renderContent()}
            </div>
        </div>
    );
};

export default GameComponent;
