import styled from 'styled-components';

import EpicGamesBackgroundImg from '../../assets/EpicGames/images/EpicGamesBackground.svg';

const EpicGamesBackground = styled.div`
    width: 100vw;
    height: 100vh;
    background: url(${EpicGamesBackgroundImg}) center/cover no-repeat;
`;

export default EpicGamesBackground;
