import React, { useState } from 'react';
import axios from 'axios';

const Register = ({ setActiveComponent }) => {
    const [login, setLogin] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [repeatPassword, setRepeatPassword] = useState('');
    const [displayName, setDisplayName] = useState('');
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [focusedField, setFocusedField] = useState(null);

    const validateEmail = (email) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return re.test(String(email).toLowerCase());
    };

    const validateLogin = (login) => {
        const re = /^[a-zA-Zа-яА-Я0-9@$!%*?&^#-_]{3,50}$/;
        return re.test(login);
    };

    const validatePassword = (password) => {
        const re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/;
        return re.test(password);
    };

    const validateForm = () => {
        const newErrors = {};
        if (!login) newErrors.login = 'Логин обязателен';
        else if (!validateLogin(login)) newErrors.login = 'Некорректный логин';
        if (!email) newErrors.email = 'Email обязателен';
        else if (!validateEmail(email)) newErrors.email = 'Некорректный email';
        if (!password) newErrors.password = 'Пароль обязателен';
        else if (!validatePassword(password)) newErrors.password = 'Пароль должен содержать минимум 8 символов, включая хотя бы одну заглавную букву, одну строчную букву и одну цифру.';
        if (password !== repeatPassword) newErrors.repeatPassword = 'Пароли не совпадают';
        if (!displayName) newErrors.displayName = 'Имя пользователя обязательно';

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/register`, { login, email, password, displayName });
            if (response.data && response.data.msg) {
                setSuccessMessage(response.data.msg);
                setLogin('');
                setEmail('');
                setPassword('');
                setRepeatPassword('');
                setDisplayName('');
                setErrors({});
            } else if (response.data && response.data.errors) {
                setErrors({ form: response.data.errors });
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' });
            }
        } catch (err) {
            console.error(err);
            if (err.response && err.response.data && err.response.data.errors) {
                setErrors({ form: err.response.data.errors });
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' });
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container d-flex flex-column justify-content-center align-items-center">
            <form className="w-100" onSubmit={handleSubmit} noValidate>
                <div className="mb-3">
                    {focusedField === 'login' && <label className="form-label text-start w-100 fs-6">Логин</label>}
                    <input
                        className="form-control"
                        type="text"
                        placeholder="Логин"
                        value={login}
                        onChange={(e) => setLogin(e.target.value)}
                        onFocus={() => setFocusedField('login')}
                        onBlur={() => setFocusedField(null)}
                        required
                    />
                    {errors.login && <span className="text-danger">{errors.login}</span>}
                </div>
                <div className="mb-3">
                {focusedField === 'email' && <label className="form-label text-start w-100 fs-6">Email</label>}
                    <input
                        className="form-control"
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        onFocus={() => setFocusedField('email')}
                        onBlur={() => setFocusedField(null)}
                        required
                    />
                    {errors.email && <span className="text-danger">{errors.email}</span>}
                </div>
                <div className="mb-3">
                    {focusedField === 'password' && <label className="form-label text-start w-100 fs-6">Пароль</label>}
                    <input
                        className="form-control"
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        onFocus={() => setFocusedField('password')}
                        onBlur={() => setFocusedField(null)}
                        required
                    />
                    {errors.password && <span className="text-danger">{errors.password}</span>}
                </div>
                <div className="mb-3">
                    {focusedField === 'repeatPassword' && <label className="form-label text-start w-100 fs-6">Повторите пароль</label>}
                    <input
                        className="form-control"
                        type="password"
                        placeholder="Повторите пароль"
                        value={repeatPassword}
                        onChange={(e) => setRepeatPassword(e.target.value)}
                        onFocus={() => setFocusedField('repeatPassword')}
                        onBlur={() => setFocusedField(null)}
                        required
                    />
                    {errors.repeatPassword && <span className="text-danger">{errors.repeatPassword}</span>}
                </div>
                <div className="mb-3">
                    {focusedField === 'displayName' && <label className="form-label text-start w-100 fs-6">Имя пользователя</label>}
                    <input
                        className="form-control"
                        type="text"
                        placeholder="Имя пользователя"
                        value={displayName}
                        onChange={(e) => setDisplayName(e.target.value)}
                        onFocus={() => setFocusedField('displayName')}
                        onBlur={() => setFocusedField(null)}
                        required
                    />
                    {errors.displayName && <span className="text-danger">{errors.displayName}</span>}
                </div>
                {errors.form && <div className="alert alert-danger">{errors.form}</div>}
                {successMessage && <div className="alert alert-success">{successMessage}</div>}
                <button type="submit" className="btn btn-primary w-100" disabled={loading}>
                    {loading ? 'Регистрация...' : 'Зарегистрироваться'}
                </button>
            </form>
            <button className="btn btn-link mt-3" onClick={() => setActiveComponent('login')}>
                Уже есть аккаунт? Войти
            </button>
        </div>
    );
};

export default Register;
