import React, { useState } from 'react';

import Login from './Login';
import Register from './Register';
import ForgotPassword from './ForgotPassword';

const Modal = ({ closeModal }) => {
    const [activeComponent, setActiveComponent] = useState('login');

    const renderComponent = () => {
        switch (activeComponent) {
            case 'login':
                return <Login setActiveComponent={setActiveComponent} />;
            case 'register':
                return <Register setActiveComponent={setActiveComponent} />;
            case 'forgotPassword':
                return <ForgotPassword setActiveComponent={setActiveComponent} />;
            default:
                return null;
        }
    };

    const handleOverlayClick = (e) => {
        if (e.target === e.currentTarget) {
            closeModal();
        }
    };

    // Определяем заголовок в зависимости от активного компонента
    const getTitle = () => {
        switch (activeComponent) {
            case 'login':
                return 'Авторизация';
            case 'register':
                return 'Регистрация';
            case 'forgotPassword':
                return 'Запрос на восстановление пароля';
            default:
                return '';
        }
    };

    return (
        <div className="modal show d-block" tabIndex="-1" role="dialog" onClick={handleOverlayClick}>
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content bg-dark text-white">
                    <div className="modal-header">
                        <h5 className="modal-title">{getTitle()}</h5>
                        <button onClick={closeModal} className='btn-close btn-close-epic-theme'></button>
                    </div>
                    <div className="modal-body">
                        {renderComponent()}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Modal;
