import React, { useState } from 'react'
import axios from 'axios'
import { useParams, useNavigate } from 'react-router-dom'

const ResetPassword = () => {
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')
    const [focusedField, setFocusedField] = useState('')
    const { token } = useParams()
    const navigate = useNavigate()

    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/

    const handleSubmit = async (e) => {
        e.preventDefault()

        if (!passwordRegex.test(password)) {
            setError('Пароль должен содержать минимум 8 символов, включать одну заглавную букву, одну строчную букву и одну цифру.')
            return
        }

        if (password !== confirmPassword) {
            setError('Пароли не совпадают')
            return
        }

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/reset-password/${token}`, { password })
            setMessage(response.data.msg)
            setError('')
            setTimeout(() => navigate('/'), 3000)
        } catch (err) {
            setError('Ошибка при сбросе пароля. Попробуйте еще раз.')
            setMessage('')
        }
    }

    return (
        <div className='modal show d-block'>
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content bg-dark text-white">
                    <div className='modal-header'>
                        <h5 className='modal-title'>Сброс пароля</h5>
                    </div>
                    <div className="modal-body">
                        <div className="container d-flex flex-column justify-content-center align-items-center">
                            <form className='w-100' onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    {focusedField === 'password' && <label className="form-label text-start w-100 fs-6">Ваш пароль</label>}
                                    <input
                                        type="password"
                                        placeholder="Новый пароль"
                                        className="form-control"
                                        value={password}
                                        onFocus={() => setFocusedField('password')}
                                        onBlur={() => setFocusedField('')}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    {focusedField === 'confirmPassword' && <label className="form-label text-start w-100 fs-6">Повтор пароля</label>}
                                    <input
                                        type="password"
                                        placeholder="Повтор пароля"
                                        className="form-control"
                                        value={confirmPassword}
                                        onFocus={() => setFocusedField('confirmPassword')}
                                        onBlur={() => setFocusedField('')}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <button type="submit" className='btn btn-primary w-100'>Сбросить пароль</button>
                            </form>
                            {message && <p className="alert alert-success mt-3">{message}</p>}
                            {error && <p className="alert alert-danger mt-3">{error}</p>}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ResetPassword
