// Импорт библиотек
import React, { useState, useEffect } from 'react';

// Импорт роутеров
import { BrowserRouter as Router } from 'react-router-dom';

// Импорт провайдеров
import { UserProvider } from './contexts/UserContext.js';
import { ScreenProvider } from './contexts/ScreenContext.js';
import { ThemeProvider } from 'styled-components';

// Импорт сервисов
import { checkToken } from './services/authService.js';

//Ипорт стилей
import GlobalStyle from './style/GlobalStyle.js';
import { darkThemeEpicGames } from './style/themes.js';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [theme, setTheme] = useState(darkThemeEpicGames);

  useEffect(() => {
    const verifyToken = async () => {
      const isAuth = await checkToken();
      setIsAuthenticated(isAuth);
    };

    verifyToken();

    const intervalId = setInterval(verifyToken, 60000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <Router>
      <UserProvider>
        <ScreenProvider>
          <ThemeProvider theme={theme}>
            <GlobalStyle />

          </ThemeProvider>
        </ScreenProvider>
      </UserProvider>
    </Router>
  );
}

export default App;
