// Dashboard.js
import './Dashboard.css';
import React, { useState, useEffect } from 'react';
import User from './User';
import Menu from './Menu';
import ContentWindow from './ContentWindow';

const Dashboard = () => {
    const [selectedComponent, setSelectedComponent] = useState(null);

    useEffect(() => {
        // Находим элемент с классом App и убираем его фон при монтировании компонента Dashboard
        const appElement = document.querySelector('.App');
        if (appElement) {
            appElement.style.background = 'none';
        }

        // Возвращаем оригинальный фон при размонтировании компонента Dashboard
        return () => {
            if (appElement) {
                appElement.style.background = "linear-gradient(to bottom, rgba(30, 37, 56, 1), rgba(30, 37, 56, 0.5), rgba(30, 37, 56, 0.25), rgba(30, 37, 56, 0) 50%, rgba(30, 37, 56, 0.25), rgba(30, 37, 56, 0.5), rgba(30, 37, 56, 1)), linear-gradient(to left, rgba(30, 37, 56, 1), rgba(30, 37, 56, 0.5), rgba(30, 37, 56, 0.25), rgba(30, 37, 56, 0) 50%, rgba(30, 37, 56, 0.25), rgba(30, 37, 56, 0.5), rgba(30, 37, 56, 1)), url('EpicBackground.png') center/cover no-repeat";
            }
        };
    }, []);

    return (
        <div className="dashboard">
            <div className="sidebar">
                <User />
                <Menu onSelect={setSelectedComponent} />
            </div>
            <div className="content-window">
                <ContentWindow selectedComponent={selectedComponent} />
            </div>
        </div>
    );
};

export default Dashboard;
