import './ForgotPassword.css'
import React, { useState } from 'react'
import axios from 'axios'

const ForgotPassword = ({ setActiveComponent }) => {
    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()

        console.log(process.env.REACT_APP_API_URL)
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/forgot-password`, { email })
            setMessage(response.data.msg)
            setError('')
        } catch (err) {
            setError('Ошибка при отправке запроса. Попробуйте еще раз.')
            setMessage('')
        }
    }

    return (
        <div className="container">
            <h2 className="title">Восстановление пароля</h2>
            <form className='form-container' onSubmit={handleSubmit}>
                    <input
                        type="email"
                        placeholder="Логин, почта или телефон" 
                        className="input-field"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                <button type="button">Отправить запрос</button>
            </form>
            <button onClick={() => setActiveComponent('login')}>Назад к Входу</button>
            <button onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
            {message && <p className="success-message">{message}</p>}
            {error && <p className="error-message">{error}</p>}
        </div>
    )
}

export default ForgotPassword
