import React, { useState } from "react";
import styled from "styled-components";
import EpicGamesLogotype from './EpicGamesLogotype';
import { useScreen } from '../../contexts/ScreenContext';
import closeFormButtonSVG from '../../assets/EpicGames/images/CloseFormButton.svg';

const Modal = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: ${({ $orientation, $device }) => ($orientation === 'landscape' && $device === 'mobile' ? '100%' : '50%')};
    height: ${({ $orientation, $device }) => ($orientation === 'landscape' && $device === 'mobile' ? '100%' : '50%')};
    min-width: ${({ $device }) => ($device === 'mobile' ? '100%' : '280px')};
    max-width: ${({ $device }) => ($device === 'mobile' ? '100%' : '357px')};
    min-height: ${({ $device }) => ($device === 'mobile' ? '100%' : '411px')};
    max-height: ${({ $device }) => ($device === 'mobile' ? '100%' : '516px')};
    background-color: rgba(21, 23, 30, 0.9);
    padding: 20px;
    border-radius: ${({ $orientation, $device }) => ($orientation === 'landscape' && $device === 'mobile' ? '16px' : '8px')};
    z-index: 1000;
`;

const Overlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(40, 57, 101, 0.85);
    z-index: 999;
`;

const Header = styled.header`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    padding: 10px;
    margin-left: 10px;
`;

const CloseButton = styled.button`
    position: absolute;
    top: ${({ $device }) => ($device === 'mobile' ? '30px' : '10px')};
    right: ${({ $device }) => ($device === 'mobile' ? '30px' : '10px')};
    width: 30px;
    height: 30px;
    background: url(${closeFormButtonSVG}) no-repeat center center;
    background-size: contain;
    border: none;
    cursor: pointer;
    transition: opacity 0.3s;

    &:hover {
        filter: invert(29%) sepia(86%) saturate(7494%) hue-rotate(355deg) brightness(104%) contrast(101%);
    }
`;

const ModalFormContainer = ({ closeModal }) => {
    const { orientation, device } = useScreen();
    const [formState, setFormState] = useState('login'); // 'login', 'register', 'requestReset', 'resetPassword'

    const renderFormContent = () => {
        switch (formState) {
            case 'login':
                return <div>Форма авторизации</div>;
            case 'register':
                return <div>Форма регистрации</div>;
            case 'requestReset':
                return <div>Запрос на восстановление пароля</div>;
            case 'resetPassword':
                return <div>Восстановление пароля</div>;
            default:
                return null;
        }
    };

    return (
        <>
            <Overlay onDoubleClick={closeModal} />
            <Modal $orientation={orientation} $device={device}>
                <Header>
                    <EpicGamesLogotype isLink={false} hasText={true} horizontalOnly={false} forTheForm={true} />
                    <CloseButton onClick={closeModal} $orientation={orientation} $device={device} />
                </Header>
                <main>
                    {renderFormContent()}
                </main>
            </Modal>
        </>
    );
};

export default ModalFormContainer;
