// GamePartsConstructor.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GamePartsConstructor = ({ game }) => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/${game.id}/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, [game]);

    return (
        <div>
            <h2>Конструктор частей/глав/уровней</h2>
            {error && <p className="error">{error}</p>}
            <button onClick={handleAddPart}>Создать новую часть</button>
            <ul>
                {parts.map(part => (
                    <li key={part.id}>
                        {part.name}
                        <button onClick={() => handleEditPart(part.id)}>Изменить</button>
                        <button onClick={() => handleDeletePart(part.id)}>Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default GamePartsConstructor;
