// User.js
import React, { useContext } from 'react';
import { UserContext } from '../UserContext';

const User = () => {
    const { user } = useContext(UserContext);

    return (
        <div className='profile'>
            <div className='user-display-name'>
                {user ? <p>{user.displayName}</p> : <p>Информация о пользователе не доступна.</p>}
            </div>
            <div className='user-avatar'>
                {user ? (
                    <img className='avatar-image' src={`../img/avatars/${user.avatarFilename}`} alt="User Avatar" />
                ) : (
                    <p>Аватарка не доступна.</p>
                )}
            </div>
        </div>
    );
};

export default User;
