// GamePartsConstructor.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
    width: 80vw;
    height: 90vh;
    display: flex;
    flex-direction: row;
    margin: 20px;
`;

const ConstructorList = styled.ul`
    width: 20vw;
    height: auto;
    display: flex;
    flex-direction: column;
    margin: 10px;
    padding: 10px;
    color: #FFFFFF;
`;

const Constructors = styled.h2`
    color: #FFFFFF;
    font-weight: 600;
    margin: 10px;
`;

const ConstructorButton = styled.button`
    color: #FFFFFF;
    font-weight: 600;
    background-color: transparent;
    border: none;
    cursor: pointer;
    padding: 5px;
    text-align: left;

    &:hover {
        text-decoration: underline;
    }
`;

const ConstructorWindowContainer = styled.div`
    width: 60vw;
    height: auto;
    margin: 10px;
    padding: 10px;
`;

const ErrorMessage = styled.p`
    color: red;
`;

const GamePartsConstructor = ({ game }) => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/${game.id}/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, [game]);

    const handleAddPart = () => {
        // Логика добавления части
    };

    const handleEditPart = (partId) => {
        // Логика редактирования части
    };

    const handleDeletePart = (partId) => {
        // Логика удаления части
    };

    return (
        <Container>
            <ConstructorWindowContainer>
                <Constructors>Конструктор частей/глав/уровней</Constructors>
                {error && <ErrorMessage>{error}</ErrorMessage>}
                <ConstructorButton onClick={handleAddPart}>Создать новую часть</ConstructorButton>
                <ConstructorList>
                    {parts.map(part => (
                        <li key={part.id}>
                            {part.name}
                            <ConstructorButton onClick={() => handleEditPart(part.id)}>Изменить</ConstructorButton>
                            <ConstructorButton onClick={() => handleDeletePart(part.id)}>Удалить</ConstructorButton>
                        </li>
                    ))}
                </ConstructorList>
            </ConstructorWindowContainer>
        </Container>
    );
};

export default GamePartsConstructor;
