// GamePartsConstructor.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const styles = {
    container: {
        width: '80vw',
        height: '90vh',
        display: 'flex',
        flexDirection: 'row',
        margin: '20px'
    },
    constructorList: {
        width: '20vw',
        height: 'auto',
        display: 'flex',
        flexDirection: 'column',
        margin: '10px',
        padding: '10px',
        color: '#FFFFFF'
    },
    constructors: {
        color: '#FFFFFF',
        fontWeight: 600,
        margin: '10px'
    },
    constructorButton: {
        color: '#FFFFFF',
        fontWeight: 600,
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer',
        padding: '5px',
        textAlign: 'left'
    },
    constructorWindowContainer: {
        width: '60vw',
        height: 'auto',
        margin: '10px',
        padding: '10px'
    },
    errorMessage: {
        color: 'red'
    }
};

const GamePartsConstructor = ({ game }) => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/${game.id}/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, [game]);

    const handleAddPart = () => {
        // Логика добавления части
    };

    const handleEditPart = (partId) => {
        // Логика редактирования части
    };

    const handleDeletePart = (partId) => {
        // Логика удаления части
    };

    return (
        <div style={styles.container}>
            <div style={styles.constructorWindowContainer}>
                <h2 style={styles.constructors}>Конструктор частей/глав/уровней</h2>
                {error && <p style={styles.errorMessage}>{error}</p>}
                <button style={styles.constructorButton} onClick={handleAddPart}>Создать новую часть</button>
                <ul style={styles.constructorList}>
                    {parts.map(part => (
                        <li key={part.id}>
                            {part.name}
                            <button style={styles.constructorButton} onClick={() => handleEditPart(part.id)}>Изменить</button>
                            <button style={styles.constructorButton} onClick={() => handleDeletePart(part.id)}>Удалить</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default GamePartsConstructor;
