import React, { useState } from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Label = styled.label`
    margin-bottom: 8px;
    padding-left: 10px;
    font-size: 20px;
    font-weight: 400;
    color: #FFFFFF;
    line-height: 150%;
    letter-spacing: 0.4px;
`;

const Input = styled.input`
    width: auto;
    height: 25px;
    margin-bottom: 16px;
    padding: 8px;
    border: 1px solid #4A4C50;
    border-radius: 5px;

    font-size: 18px;
    font-weight: 545;
    color: #FFFFFF;
    line-height: 150%;
    letter-spacing: 0.4px;
`;

const Button = styled.button`
    margin: 4px;
    padding: 8px 16px;
    background-color: #007BFF;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;

    &:hover {
        background-color: #0056b3;
    }
`;

const LoginForm = ({ onSubmit, onChangeFormState }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { orientation, device } = useScreen();

    const handleSubmit = (event) => {
        event.preventDefault();
        onSubmit({ email, password });
    };

    return (
        <Form onSubmit={handleSubmit}>
            <FormContainer>
                <Label>Логин/Email:</Label>
                <Input 
                    type="email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)} 
                    required 
                    $orientation={orientation}
                    $device={device}
                    placeholder="Логин/Email:"
                />
            </FormContainer>
            <FormContainer>
                <Label>Пароль:</Label>
                <Input 
                    type="password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)} 
                    required 
                    $orientation={orientation}
                    $device={device}
                    placeholder="Пароль:"
                />
            </FormContainer>
            <Button type="submit">Авторизация</Button>
            <Button type="button" onClick={() => onChangeFormState('register')}>Зарегистрируйте учетную запись</Button>
            <Button type="button" onClick={() => onChangeFormState('requestReset')}>Вы не можете войти?</Button>
        </Form>
    );
};

export default LoginForm;
