import React, { useState } from 'react';
import './Modal.css';

import Login from './Login';
import Register from './Register';
import ForgotPassword from './ForgotPassword';

const modalOverlay = {
    position: 'fixed',
    top: '0',
    left: '0',
    width: '100%',
    height: '100%',
    background: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 999
}

const Modal = ({ closeModal }) => {
    const [activeComponent, setActiveComponent] = useState('login');

    const renderComponent = () => {
        switch (activeComponent) {
            case 'login':
                return <Login setActiveComponent={setActiveComponent} />;
            case 'register':
                return <Register setActiveComponent={setActiveComponent} />;
            case 'forgotPassword':
                return <ForgotPassword setActiveComponent={setActiveComponent} />;
            default:
                return null;
        }
    };

    const handleOverlayClick = (e) => {
        if (e.target === e.currentTarget) {
            closeModal();
        }
    };

    return (
        <div style={ modalOverlay } onClick={handleOverlayClick}>
            <div className="modal">
                <div className="header-modal-form">
                    <button className="close-form" onClick={closeModal}>âœ–</button>
                </div>
                {renderComponent()}
            </div>
        </div>
    );
};

export default Modal;
