import './Login.css';
import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setActiveComponent, setIsAuthenticated }) => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/login`, { identifier, password });
            if (response.data.errors) {
                setErrors(response.data.errors);
            } else {
                localStorage.setItem('token', response.data.token);
                setIsAuthenticated(true);
                window.location.href = '/dashboard';
            }
        } catch (err) {
            console.error('Ошибка при авторизации:', err);
            setErrors('Произошла ошибка при авторизации');
        }
    };

    return (
        <div className="container">
            <h2 className="title">Войти</h2>
            <form className='form-container' onSubmit={handleLogin}>
                <input 
                    type="text" 
                    placeholder="Логин, почта или телефон" 
                    className="input-field"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required 
                />
                <input 
                    type="password" 
                    placeholder="Ваш пароль" 
                    className="input-field"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required 
                />
                <button type="submit" className="button-auth">Войти</button>
            </form>
            <button className="button-register" onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
            <a href="#" className="forgot-password" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</a>
            {errors && <div className="error">{errors}</div>}
        </div>
    );
};

export default Login;
