// GamePartsConstructor.js
import './GamePartsConstructor.css'

import React, { useState, useEffect } from 'react';
import axios from 'axios';

import svgEdit from '../../assets/icons/edit.svg';
import svgDelete from '../../assets/icons/delete.svg';
import svgNewDocument from '../../assets/icons/new_document.svg';

import PartForm from './PartForm';

import { UserContext } from '../../contexts/UserContext';

const GamePartsConstructor = ({ game }) => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');
    const { user } = useContext(UserContext);

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/${game.id}/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, [game]);

    const [isFormVisible, setIsFormVisible] = useState(false);
    const [selectedPart, setSelectedPart] = useState(null);

    const handleSavePart = async (partData) => {
        console.log(partData)
        // try {
        //     const token = localStorage.getItem('token');
        //     await axios.post(`${process.env.REACT_APP_API_URL}/api/save-part`, partData, {
        //         headers: { Authorization: `Bearer ${token}` }
        //     });
        //     setIsFormVisible(false);
        //     fetchParts();  // Обновите список частей после сохранения
        // } catch (err) {
        //     console.error('Ошибка при сохранении части:', err);
        //     setError('Не удалось сохранить часть');
        // }
    };

    const handleAddPart = () => {
        const dataPart = {
            game: game,
            part: null,
            author: user
        }
        setSelectedPart(dataPart);
        setIsFormVisible(true);
    };

    const handleEditPart = (part) => {
        const dataPart = {
            game: game,
            part: part,
            author: part.author
        }
        setSelectedPart(dataPart);
        setIsFormVisible(true);
    };

    const handleDeletePart = (part) => {
        const partId = part.id;
        console.log(partId)
        // Логика удаления части
    };

    return (
        <div>
            <h2 className='title-constructor'>Конструктор частей/глав</h2>
            {error && <p className="error">{error}</p>}
            <button className='button-new-document' onClick={() => handleAddPart(game)}>Создать <img src={svgNewDocument} alt='Добавить' /></button>
            <div className='constructor-window-container'>
                <ul className='elements-list-from-server'>
                    {parts.map(part => (
                        <li className='li-element-lists' key={part.id}>
                            <div className='part-container'>
                                <div className='part-name'>
                                    <span>Наименование:</span>{part.name}
                                </div>
                                <div className='game-info-container'>
                                    <span>Игра:</span>{part.game.name}
                                </div>
                                <div className='autor-info-container'>
                                    <div><span>Логин:</span>{part.author.login}</div>
                                    <div><span>e-mail:</span>{part.author.email}</div>
                                </div>
                            </div>
                            <div className='button-group-container'>
                                <button className='button-edit-document' onClick={() => handleEditPart(part)}><img src={svgEdit} alt='Редактировать' /></button>
                                <button className='button-delete-document' onClick={() => handleDeletePart(part)}><img src={svgDelete} alt='Удалить' /></button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            {isFormVisible && (
                <PartForm
                    initialData={selectedPart}
                    onSave={handleSavePart}
                    onCancel={() => setIsFormVisible(false)}
                />
            )}
        </div>
    );
};

export default GamePartsConstructor;
