import React, { useState } from 'react'
import axios from 'axios'

const ForgotPassword = ({ setActiveComponent }) => {
    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')
    const [focusedField, setFocusedField] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/forgot-password`, { email })
            setMessage(response.data.msg)
            setError('')
        } catch (err) {
            setError('Ошибка при отправке запроса. Попробуйте еще раз.')
            setMessage('')
        }
    }

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4">
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                {focusedField === 'email' && <label className="form-label">Ваш e-mail</label>}
                                <input
                                    type="email"
                                    placeholder="e-mail" 
                                    className="form-control"
                                    value={email}
                                    onFocus={() => setFocusedField('email')}
                                    onBlur={() => setFocusedField('')}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </div>
                            <button type="submit" className='btn btn-primary w-100'>Отправить запрос</button>
                        </form>
                        <button className='btn btn-link w-100 mt-2' onClick={() => setActiveComponent('login')}>Назад к Входу</button>
                        <button className='btn btn-link w-100' onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
                        {message && <p className="alert alert-success mt-3">{message}</p>}
                        {error && <p className="alert alert-danger mt-3">{error}</p>}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ForgotPassword
