import styled from "styled-components";

const Button = styled.button`
    width: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '60%' : '40%')};
    height: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '10%' : '20%')};
    margin-bottom: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '10vmin' : '5vmin')};
    margin-left: 1vmin;
    margin-right: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '5vmin' : '1vmin')};
    border-radius: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '2vmin' : '1vmin')}; 
    font-size: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '3vmin' : '2vmin')}; 
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
        background-color: transparent;
        box-shadow: 0 0 10px 2px ${(props) => props.hoverColor};
        border-width: 2px;
    }
`;

export const LoginButton = styled(Button).attrs({
    hoverColor: 'rgba(46, 204, 113, 0.7)'
})`
    background-color: #2ECC71;
    color: #FFFFFF;
    border: 1px solid #2ECC71;
`;

export const RegisterButton = styled(Button).attrs({
    hoverColor: 'rgba(52, 152, 219, 0.7)'
})`
    background-color: #3498DB;
    color: #FFFFFF;
    border: 1px solid #3498DB;
`;
