import React, { useContext } from "react";
import styled from "styled-components";
import EpicGamesBackground from "./EpicGamesBackground";
import { UserContext } from '../../contexts/UserContext';

const Container = styled.div`
    width: 100vw;
    height: 20%;
    position: fixed;
    bottom: 0;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Button = styled.button`
    width: 15vw;
    margin: 0 3vw;
    padding: 10px 20px;
    border-radius: 10px;
    font-size: 18px;
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
        background-color: transparent;
        box-shadow: 0 0 10px 2px ${(props) => props.hoverColor};
        border-width: 2px;
    }
`;

const LoginButton = styled(Button)`
    background-color: #2ECC71;
    color: #FFFFFF;
    border: 1px solid #2ECC71;

    &:hover {
        hoverColor: rgba(46, 204, 113, 0.7);
    }
`;

const RegisterButton = styled(Button)`
    background-color: #3498DB;
    color: #FFFFFF;
    border: 1px solid #3498DB;

    &:hover {
        hoverColor: rgba(52, 152, 219, 0.7);
    }
`;

const EpicGamesStartAppButtonContainer = () => {
    return (
        <Container>
            <LoginButton>Войти в аккаунт</LoginButton>
            <RegisterButton>Зарегистрироваться</RegisterButton>
        </Container>
    );
}

const EpicGamesStartApp = () => {
    return (
        <>
            <EpicGamesBackground />
            <EpicGamesStartAppButtonContainer />
        </>
    )
}

const EpicGamesAppPlatform = () => {
    return (
        <>
            test
        </>
    )
}

const AppContent = () => {
    const { isAuthenticated } = useContext(UserContext)

    return (
        <>
            {isAuthenticated ? <EpicGamesAppPlatform /> : <EpicGamesStartApp />}
        </>
    )
}

export default AppContent;
