import React, { useState, useContext } from "react";
import styled from "styled-components";
import { useScreen } from '../../contexts/ScreenContext';
import { UserContext } from "../../contexts/UserContext";

import EpicGamesContentGames from "../EpicGames/EpicGamesContentGames";

import EpicGamesLogotypeImage from '../../assets/EpicGames/images/EpicGamesLogotype.png';
import NotificationBellsvg from '../../assets/EpicGames/images/NotificationBell.svg';
import StandartAvatar from '../../assets/avatars/standart.png';
import Arrowsvg from '../../assets/EpicGames/images/arrow.svg';

const AppContainer = styled.div`
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    position: fixed;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    //height: ${({ $orientation }) => ($orientation === 'portrait' ? '100vmax' : '100vmin')};
    height: 100%;
    background-color: #454648;
    overflow: hidden;
`;

const PlatformContainer = styled.div`
    position: relative;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    flex-direction: column;
    width: ${({ $orientation }) => ($orientation === 'portrait' ? '98vw' : 'calc(98lvh*(16/9))')};
    height: ${({ $orientation, $device }) => ($orientation === 'portrait' ? 'calc(98vw*(9/16))' : ($device === 'mobile' ? '98svh' : 'auto'))};
    aspect-ratio: 16 / 9;
    background-color: #15171E;
`;

const Header = styled.div`
    width: 100%;
    height: 5.24vw;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 1.7vw;
    margin: 0.59vw 0;
    margin-top: 1.7vw;
    box-sizing: border-box;
`;

const LogoMenuContainer = styled.div`
    display: flex;
    align-items: center;
    width: 67.31vw;
`;

const StyledLink = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const LogoImage = styled.img`
  position: relative;
  width: 5.24vw;
  height: auto;
`;

const Nav = styled.nav`
    display: flex;
    justify-content: flex-start;
    align-items: center;
    width: 100%;
`;

const MenuList = styled.ul`
    display: flex;
    list-style: none;
    padding: 0;
    margin: 0;
`;

const MenuItem = styled.li`
    font-size: 1.2vw;
    font-weight: 500;
    color: ${({ $active }) => ($active ? "#FFFFFF" : "#86898E")};
    cursor: pointer;
    text-transform: uppercase;
    user-select: none;
    position: relative;
    margin: 0 1.5vw;

    &:hover {
        color: #FFFFFF;
    }

    ${({ $active }) =>
        $active &&
        `
        &::after {
            content: '';
            position: absolute;
            left: -.2vw;
            right: -.2vw;
            bottom: -.4vw;
            height: .2vw;
            background-color: #148EFF;
        }
    `}
`;

const BellUserContainer = styled.div`
    display: flex;
    align-items: center;
`;

const NotificationBell = styled.div`
    position: relative;
    width: 1.77vw;
    height: 1.77vw;
    background-image: url(${NotificationBellsvg});
    background-size: cover;
    cursor: pointer;
`;

const NotificationCount = styled.div`
    position: absolute;
    bottom: -0.3vw;
    right: -0.3vw;
    background-color: red;
    color: white;
    border-radius: 50%;
    padding: .2vw;
    font-size: 0.6vw;
    width: .5vw;
    height: .5vw;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const UserHeader = styled.div`
    width: 27.7vw;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding-left: 2vw;
    position: relative;
    z-index: 1000;
`;

const Avatar = styled.img`
  width: 5vw;
  height: auto;
  border-radius: 50%;
`;

const VerticalContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Login = styled.span`
  font-size: 1.2vw;
  color: #74B8E9;
  margin-left: 1vw;
`;

const Status = styled.span`
  font-size: 1.2vw;
  margin-left: 1vw;
  color: ${({ $online }) => ($online ? 'green' : '#86898E')};
`;

const MenuUserContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const MenuButton = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: transparent;
  border: none;
  cursor: pointer;
  transform: ${({ $isOpen }) => ($isOpen ? 'rotate(90deg)' : 'rotate(0deg)')};
  transition: transform 0.3s ease;
  background-image: url(${Arrowsvg});
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  width: 0.88vw;
  height: 0.88vw;
  filter: ${({ $isOpen }) => ($isOpen ? 'none' : 'invert(50%)')};
  margin-left: 0.5vw;
`;

const MenuUser = styled.div`
  position: absolute;
  top: 2vw;
  right: -2vw;
  background-color: #15171E;
  border: none;
  box-shadow: 0 0.2vw 0.5vw rgba(0, 0, 0, 0.1);
  display: ${({ $isOpen }) => ($isOpen ? 'block' : 'none')};
`;

const MenuItemUser = styled.button`
  display: block;
  width: 100%;
  padding: 1vw;
  background-color: transparent;
  border: none;
  text-align: left;
  cursor: pointer;
  color: white;

  &:hover {
    background-color: #24252B;
  }
`;

const Container = styled.div`
    width: 100%;
    padding: 0 1.7vw;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
    box-sizing: border-box;
`;

const FirstSection = styled.div`
    display: flex;
    flex-direction: column;
    width: 100%;
`;

const Tooltip = styled.div`
  position: absolute;
  background-color: #333;
  color: #fff;
  padding: 1vw;
  margin-top: 0.3vw;
  border-radius: .5vw;
  white-space: nowrap;
  font-size: 1.2vw;
  opacity: 1;
  transform: translateX(-27%);
  z-index: 1000;
`;

const FavoriteAndActiveGamesMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: flex-start;
    height: 4.45vw;
    padding-left: 1vw;
    padding-right: 1vw;
    background-color: #24252B;
    border-radius: .4vw;
    position: relative;
    
    div {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        overflow: hidden;
    }
`;

const Title = styled.h2`
  color: #86898E;
  font-size: 1vw;
  font-weight: 600;
  margin: 0;
  padding-right: 1vw;
`;

const FavoriteAdd = styled.button`
  width: 3vw;
  height: 3vw;
  background-color: #2F343B;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 2.4vw;
  border: none;
  cursor: pointer;
  position: relative;
  margin-left: 1vw;
  padding: 0.5vw 1vw;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #3B4046;
  }

  &:hover > span {
    transform: scale(1.1);
  }
`;

const GameList = styled.ul`
  display: flex;
  list-style-type: none;
  padding: 0;
  margin: 0;
  overflow: hidden;
`;

const NavButton = styled.button`
  background-color: transparent;
  border: none;
  color: #86898E;
  font-size: 2vw;
  cursor: pointer;
  &:hover {
    color: #fff;
  }
  &:disabled {
    color: #86898E;
    cursor: not-allowed;
  }
`;

const ContentDisplay = styled.div`
    // Стили для отображения контента
`;

const SecondSection = styled.div`
    display: flex;
    flex-direction: column;
`;

const FriendsBlock = styled.div`
    // Стили для блока с друзьями
`;

const ChatBlock = styled.div`
    // Стили для чата
`;

const StyledFavoriteButton = styled.button`
  margin-left: 0.5vw;
  margin-right: 0.5vw;
  font-size: 1vw;
  cursor: pointer;
  background-color: transparent;
  border: none;
  z-index: 10;

  img {
    width: 4vw;
    height: 4vw;
    transition: transform 0.3s ease;
  }

  &:hover {
    background-color: #2F333A;
    border-radius: .4vw;
  }

  &:hover img {
    transform: scale(1.1);
  }
`;

const FavoriteButton = ({ game }) => {
    const [isHovered, setIsHovered] = useState(false);

    return (
        <>
            <StyledFavoriteButton
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
            >
                <img src={game.icon} alt={game.name} />
                {isHovered && <Tooltip>{game.name}</Tooltip>}
            </StyledFavoriteButton>

        </>
    );
};

const PlatformApp = () => {
    const { orientation, device } = useScreen();
    const { user, logout } = useContext(UserContext);
    const [activeMenu, setActiveMenu] = useState('home');
    const [menuOpen, setMenuOpen] = useState(false);
    const [currentIndex, setCurrentIndex] = useState(0);

    const handlePrev = () => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };

    const handleNext = () => {
        if (currentIndex < user.favoriteGames.length - 1) {
            setCurrentIndex(currentIndex + 1);
        }
    };

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const settingsUser = () => {
        setActiveMenu('settingsUser');
        setMenuOpen(false);
    }

    const gamesList = [{
        id: 1,
        name: 'Гарри Поттер',
        poster: '',
        icon: '../../assets/EpicGames/images/IconGames/HarryPotter.png',
        description: 'test карточка игры гарри поттер'
    }];

    let notificationCount = 0;

    return (
        <AppContainer $orientation={orientation}>
            <PlatformContainer $orientation={orientation} $device={device}>
                
            </PlatformContainer>
        </AppContainer>
    );
};

export default PlatformApp;
