import React, { useState } from "react";
import styled from "styled-components";
import { useScreen } from '../../contexts/ScreenContext';
import { LoginButton, RegisterButton } from './EpicGamesStartAppButton';
import ModalComponent from './ModalComponent'; // Импортируйте новый компонент

const Container = styled.div`
    position: absolute;
    bottom: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? 'auto' : '0')};
    right: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '0' : 'auto')};
    width: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '30%' : '100%')};
    min-width: 300px;
    height: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '100%' : '20%')};
    min-height: 200px;
    flex-direction: ${({ $orientation, $wMin }) => (($orientation === 'landscape' && $wMin <= 767) ? 'column' : 'row')};
    bottom: 0;
    display: flex;
    justify-content: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? 'flex-end' : 'center')};
    align-items: flex-end;
`;

const EpicGamesStartAppButtonContainer = ({ setHideByModality }) => {
    const { orientation, wMin } = useScreen();
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
        setHideByModality(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setHideByModality(false);
    };

    return (
        <>
            {!isModalOpen && (
                <Container $orientation={orientation} $wMin={wMin}>
                    <LoginButton onClick={openModal}>Войти в аккаунт</LoginButton>
                    <RegisterButton onClick={openModal}>Зарегистрироваться</RegisterButton>
                </Container>
            )}
            {isModalOpen && (
                <ModalComponent closeModal={closeModal} />
            )}
        </>
    );
};

export default EpicGamesStartAppButtonContainer;
