import './App.css'
import React, { useState } from 'react'
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom'

import Register from './components/Register'
import Login from './components/Login'
import ForgotPassword from './components/ForgotPassword'
import ResetPassword from './components/ResetPassword'

const App = () => {
    const [activeComponent, setActiveComponent] = useState('register')
    const navigate = useNavigate()

    return (
        <Router>
            <div className='App'>
                <header className='App-header'></header>
                <main className='App-main'>
                    <Routes>
                        <Route path="/reset-password/:token" element={<ResetPassword />} />
                        <Route path="/" element={
                            <div>
                                <div className='Registration-form'>
                                    {activeComponent === 'register' && <Register />}
                                    {activeComponent === 'login' && <Login />}
                                    {activeComponent === 'forgotPassword' && <ForgotPassword />}
                                </div>

                                <div className='buttons'>
                                    {activeComponent !== 'login' && <button onClick={() => setActiveComponent('login')}>Авторизация</button>}
                                    {activeComponent !== 'forgotPassword' && <button onClick={() => setActiveComponent('forgotPassword')}>Восстановление пароля</button>}
                                    {activeComponent !== 'register' && <button onClick={() => setActiveComponent('register')}>Регистрация</button>}
                                </div>
                            </div>
                        } />
                    </Routes>
                </main>
                <footer></footer>
            </div>
        </Router>
    )
}

export default App
