import styled from 'styled-components';
import Button from '../ui/Button';

export const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 3rem;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 1rem;
  color: ${props => props.theme.text};
`;

const SectionSubtitle = styled.p`
  font-size: 1.25rem;
  color: ${props => props.theme.textSecondary};
  margin-bottom: 2rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-bottom: 3rem;
`;

const HeroSectionComponent = () => {
  return (
    <HeroSection>
      <SectionTitle>Переведенные Настольные Игры</SectionTitle>
      <SectionSubtitle>
        Собирайтесь с друзьями, играйте в любимые настольные игры в удобном онлайн-формате
      </SectionSubtitle>
      <ButtonGroup>
        <Button primary>Начать Играть</Button>
        <Button>Просмотреть Каталог</Button>
      </ButtonGroup>
    </HeroSection>
  );
};

export default HeroSectionComponent;
