import React from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

import EpicGamesLogotype from '../../assets/EpicGames/images/EpicGamesLogotype.png';
import EpicGamesLogotypeText from '../../assets/EpicGames/images/EpicGamesLogotypeText.svg';

// Стили для горизонтальной и вертикальной ориентации
const LogoContainer = styled.div`
  display: flex;
  flex-direction: ${({ orientation }) => (orientation === 'landscape' ? 'row' : 'column')};
  align-items: center;
`;

const LogoImage = styled.img`
  width: 50px; // Пример размера, можно настроить
  height: auto;
`;

const LogoText = styled.img`
  margin-left: ${({ orientation }) => (orientation === 'landscape' ? '10px' : '0')};
  margin-top: ${({ orientation }) => (orientation === 'portrait' ? '10px' : '0')};
  width: 100px; // Пример размера, можно настроить
  height: auto;
`;

const EpicGamesLogo = ({ hasText = true, isLink = false, linkUrl = '#' }) => {
  const { orientation } = useScreen();

  return (
    <LogoContainer orientation={orientation}>
      {isLink ? (
        <a href={linkUrl}>
          <LogoImage src={EpicGamesLogotype} alt="Epic Games Logo" />
        </a>
      ) : (
        <LogoImage src={EpicGamesLogotype} alt="Epic Games Logo" />
      )}
      {hasText && <LogoText src={EpicGamesLogotypeText} alt="Epic Games Logotype Text" orientation={orientation} />}
    </LogoContainer>
  );
};

export default EpicGamesLogo;
