import React from 'react';
import styled from 'styled-components';

const ErrorMessage = styled.div`
    width: calc(100% - 40px); /* Уменьшение ширины на 20px с каждой стороны */
    padding: 10px;
    display: flex;
    justify-content: center;
    color: red;
    margin: 10px 20px; /* Увеличение бокового отступа */
    font-size: 18px;
    background-color: #666666; /* Сделать фон еще темнее */
    border: 1px solid #CCCCCC; /* Граница цвета текущего фона */
`;

const Errors = ({ message }) => {
    return message ? <ErrorMessage>{message}</ErrorMessage> : null;
};

export default Errors;
