import React from "react";
import styled from "styled-components";
import { useScreen } from '../../contexts/ScreenContext';

const Container = styled.div`
    position: absolute;
    bottom: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? 'auto' : '0')};
    right: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '0' : 'auto')};
    width: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '30%' : '100%')};
    height: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '100%' : '20%')};
    flex-direction: ${({ $orientation, $resolution }) => (($orientation === 'landscape' && $resolution.width <= 1080) ? 'column' : 'row')};
    
    bottom: 0;
    display: flex;
    justify-content: flex-end;
    align-items: center;
`;

const Button = styled.button`
    width: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '60%' : '40%')};
    height: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '10%' : '20%')};
    margin: 20px;
    padding: 1vw 2vw;
    border-radius: 10px;
    font-size: 14px;
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
        background-color: transparent;
        box-shadow: 0 0 10px 2px ${(props) => props.hoverColor};
        border-width: 2px;
    }
`;

const LoginButton = styled(Button)`
    background-color: #2ECC71;
    color: #FFFFFF;
    border: 1px solid #2ECC71;

    &:hover {
        hoverColor: rgba(46, 204, 113, 0.7);
    }
`;

const RegisterButton = styled(Button)`
    background-color: #3498DB;
    color: #FFFFFF;
    border: 1px solid #3498DB;

    &:hover {
        hoverColor: rgba(52, 152, 219, 0.7);
    }
`;

const EpicGamesStartAppButtonContainer = () => {
    const { orientation, resolution } = useScreen();

    console.log(orientation)
    console.log(resolution.width)
    console.log(orientation === 'landscape' && resolution.width <= 1080)

    return (
        <Container $orientation={orientation} $resolution={resolution}>
            <LoginButton $orientation={orientation} $resolution={resolution}>Войти в аккаунт</LoginButton>
            <RegisterButton $orientation={orientation} $resolution={resolution}>Зарегистрироваться</RegisterButton>
        </Container>
    );
}

export default EpicGamesStartAppButtonContainer;
