import styled from 'styled-components';
import GameImage from './GameImage';
import GameContent from './GameContent';

const StyledGameCard = styled(({ theme, ...props }) => <div {...props} />)`
  background-color: ${props => props.theme.card};
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  cursor: pointer;
  position: relative;
  
  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.2);
  }
`;

// Обёртка для кнопки "Играть", чтобы она была поверх, но не мешала клику по карточке
const PlayButtonWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: opacity 0.3s ease;
  z-index: 10;

  ${StyledGameCard}:hover & {
    opacity: 1;
  }
`;

const PlayButton = styled.button`
  background: rgba(0, 0, 0, 0.7);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 1.5rem;
  font-weight: bold;
  cursor: pointer;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5);
  z-index: 11; /* Выше, чем обёртка, но карточка всё ещё кликабельна */

  &:hover {
    background: rgba(0, 0, 0, 0.9);
  }
`;

const GameCard = ({ game, theme, onClick, onPlayClick }) => {
  const handlePlayClick = (e) => {
    e.stopPropagation(); // Останавливаем всплытие, чтобы не сработал onClick карточки
    if (onPlayClick) {
      onPlayClick(game);
    }
  };

  return (
    <StyledGameCard theme={theme} onClick={onClick}>
      <PlayButtonWrapper>
        <PlayButton onClick={handlePlayClick}>
          Играть
        </PlayButton>
      </PlayButtonWrapper>
      <GameImage src={game.image} alt={game.title} />
      <GameContent game={game} theme={theme} />
    </StyledGameCard>
  );
};

export default GameCard;
