import './Login.css'
import React, { useState } from 'react'
import axios from 'axios'

const Login = () => {
    const [identifier, setIdentifier] = useState('')
    const [password, setPassword] = useState('')
    const [errors, setErrors] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/login`, { identifier, password })
            if (response.data.errors) {
                setErrors(response.data.errors)
            } else {
                // Сохранение токена и перенаправление пользователя
                localStorage.setItem('token', response.data.token)
                // Перенаправление на другую страницу или обновление состояния приложения
                window.location.href = '/dashboard'
            }
        } catch (err) {
            console.error('Ошибка при авторизации:', err)
            setErrors('Произошла ошибка при авторизации')
        }
    }

    return (
        <div>
            <h2>Авторизация</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="identifier">Email или Логин:</label>
                    <input
                        type="text"
                        id="identifier"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Войти</button>
            </form>
            {errors && <div className="error">{errors}</div>}
        </div>
    )
}

export default Login
