import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setActiveComponent }) => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/login`, { identifier, password });
            if (response.data.errors) {
                setErrors({ form: response.data.errors });
            } else {
                localStorage.setItem('token', response.data.token);
                window.location.href = '/dashboard';
            }
        } catch (err) {
            console.error('Ошибка при авторизации:', err);
            setErrors({ form: 'Произошла ошибка при авторизации' });
        }
    };

    return (
        <div className="container d-flex flex-column justify-content-center align-items-center">
            <h2 className="font-weight-bold text-white text-center mb-4">Войти</h2>
            <form className="w-100" onSubmit={handleLogin}>
                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Логин или e-mail"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required
                />
                <input
                    type="password"
                    className="form-control mb-3"
                    placeholder="Ваш пароль"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" className="btn btn-primary w-100 mb-2">Войти</button>
            </form>
            <button className="btn btn-secondary w-100 mb-2" onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
            <button className="btn btn-link" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</button>
            {errors && errors.form && (
                <div className="alert alert-danger mt-3">
                    {Array.isArray(errors.form) ? (
                        errors.form.map((error, index) => <p key={index}>{error}</p>)
                    ) : (
                        <p>{errors.form}</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Login;
