import React, { useState, useEffect, useRef } from 'react';
import { ThemeProvider as StyledThemeProvider } from 'styled-components';
import { ThemeProvider as CustomThemeProvider, useTheme } from './context/ThemeContext';
import { GlobalStyle } from './styles/GlobalStyles';
import { Container } from './components/layout/Container';
import { Main } from './components/layout/Main';

// Sections
import HeaderSection from './components/sections/HeaderSection';
import HeroSection from './components/sections/HeroSection';
import GamesGrid from './components/sections/GamesGrid';
import FeaturesSection from './components/sections/FeaturesSection';

// UI
import { ChatIcon as ChatIconButton } from './components/ui/ChatIcon';
import {
  ChatWindow,
  ChatHeader,
  ChatTabs,
  ChatTab,
  ChatMessages,
  Message,
  MessageAvatar,
  MessageContent,
  MessageInfo,
  ChatInput,
  ChatInputField,
  ChatSendButton
} from './components/ui/ChatWindow';
import MobileMenu from './components/layout/MobileMenu';

// Icons — используются только в App
import {
  MessageCircle, X, Send, Users, Gamepad2,
  User, Menu, Globe, Heart, Download, Share2,
  Home, Book, Settings, LogOut
} from 'lucide-react';

// Data
const initialGames = [
  {
    id: 1,
    title: 'Колонизаторы',
    originalTitle: 'Settlers of Catan',
    category: 'Стратегия',
    players: '3-4',
    time: '60-90 мин',
    rating: 4.8,
    downloads: 1250,
    image: 'https://placehold.co/300x200',
    description: 'Классическая стратегическая настольная игра о поселенцах на острове Катан',
    favorite: true
  },
  {
    id: 2,
    title: 'Домино',
    originalTitle: 'Dominoes',
    category: 'Фамильная',
    players: '2-4',
    time: '30-45 мин',
    rating: 4.5,
    downloads: 890,
    image: 'https://placehold.co/300x200',
    description: 'Традиционная игра с костяшками, идеально подходит для всей семьи',
    favorite: true
  },
  {
    id: 3,
    title: 'Монополия',
    originalTitle: 'Monopoly',
    category: 'Экономика',
    players: '2-8',
    time: '90-180 мин',
    rating: 4.7,
    downloads: 2100,
    image: 'https://placehold.co/300x200',
    description: 'Классическая экономическая игра о покупке и продаже недвижимости',
    favorite: false
  },
  {
    id: 4,
    title: 'Шахматы',
    originalTitle: 'Chess',
    category: 'Логика',
    players: '2',
    time: '30-120 мин',
    rating: 4.9,
    downloads: 3500,
    image: 'https://placehold.co/300x200',
    description: 'Известная логическая игра для двух игроков, проверяющая стратегическое мышление',
    favorite: true
  }
];

const initialMessages = {
  general: [
    { id: 1, user: 'Алексей', text: 'Привет! Кто-нибудь играл в "Колонизаторов" на русском?', time: '10:30', avatar: 'https://placehold.co/32x32', isOwn: false },
    { id: 2, user: 'Мария', text: 'Да, отличная игра! Особенно нравится механика торговли', time: '10:32', avatar: 'https://placehold.co/32x32', isOwn: false },
    { id: 3, user: 'Игорь', text: 'Кто-нибудь знает где можно скачать русскую версию?', time: '10:35', avatar: 'https://placehold.co/32x32', isOwn: false }
  ],
  room1: [
    { id: 1, user: 'Сергей', text: 'Кто готов к новой партии в "Домино"?', time: '11:00', avatar: 'https://placehold.co/32x32', isOwn: false },
    { id: 2, user: 'Елена', text: 'Я в игре! Когда старт?', time: '11:02', avatar: 'https://placehold.co/32x32', isOwn: false }
  ]
};

const menuItems = [
  { icon: Home, label: 'Главная', href: '#' },
  { icon: Book, label: 'Игры', href: '#' },
  { icon: Book, label: 'Магазин', href: '#' },
  { icon: Settings, label: 'Настройки', href: '#' },
  { icon: LogOut, label: 'Выйти', href: '#' }
];

const AppContent = () => {
  const { theme, toggleTheme } = useTheme(); // ✅ из context/ThemeContext

  const [isChatOpen, setIsChatOpen] = useState(false);
  const [activeChat, setActiveChat] = useState('general');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState(initialMessages);
  const [games, setGames] = useState(initialGames);
  const [isMobile, setIsMobile] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth < 768);
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, activeChat]);

  const sendMessage = () => {
    if (message.trim()) {
      const newMessage = {
        id: Date.now(),
        user: 'Вы',
        text: message,
        time: new Date().toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' }),
        avatar: 'https://placehold.co/32x32',
        isOwn: true
      };
      setMessages(prev => ({
        ...prev,
        [activeChat]: [...prev[activeChat], newMessage]
      }));
      setMessage('');
    }
  };

  const toggleChat = () => setIsChatOpen(!isChatOpen);

  const handleToggleFavorite = (gameId) => {
    setGames(prev =>
      prev.map(g => g.id === gameId ? { ...g, favorite: !g.favorite } : g)
    );
  };

  const favorites = games.filter(g => g.favorite);

  return (
    <>
      <GlobalStyle />
      <Container>
        <HeaderSection
          isMobile={isMobile}
          onToggleTheme={toggleTheme}
          onOpenMobileMenu={() => setShowMobileMenu(true)}
          favorites={favorites}
        />

        <Main>
          <HeroSection />
          <GamesGrid games={games} onToggleFavorite={handleToggleFavorite} />
          <FeaturesSection /> {/* ⬅️ добавили */}
        </Main>

        {/* Chat Icon */}
        <ChatIconButton isMobile={isMobile} onClick={toggleChat}>
          <MessageCircle size={24} />
        </ChatIconButton>

        {/* Chat Window */}
        <ChatWindow isOpen={isChatOpen} isMobile={isMobile}>
          <ChatHeader>
            <h3>Чат</h3>
            <button onClick={toggleChat}>
              <X size={20} />
            </button>
          </ChatHeader>

          <ChatTabs>
            <ChatTab
              active={activeChat === 'general'}
              onClick={() => setActiveChat('general')}
            >
              <Users size={16} /> Общий
            </ChatTab>
            <ChatTab
              active={activeChat === 'room1'}
              onClick={() => setActiveChat('room1')}
            >
              <Gamepad2 size={16} /> Комната 1
            </ChatTab>
          </ChatTabs>

          <ChatMessages>
            {messages[activeChat]?.map(msg => (
              <Message key={msg.id} isOwn={msg.isOwn}>
                <MessageAvatar src={msg.avatar} alt={msg.user} />
                <MessageContent isOwn={msg.isOwn}>
                  <div>{msg.text}</div>
                  <MessageInfo>
                    <span>{msg.user}</span>
                    <span>{msg.time}</span>
                  </MessageInfo>
                </MessageContent>
              </Message>
            ))}
            <div ref={chatEndRef} />
          </ChatMessages>

          <ChatInput>
            <ChatInputField
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              placeholder="Введите сообщение..."
            />
            <ChatSendButton onClick={sendMessage}>
              <Send size={18} />
            </ChatSendButton>
          </ChatInput>
        </ChatWindow>

        <MobileMenu
          isOpen={showMobileMenu}
          onClose={() => setShowMobileMenu(false)}
          menuItems={menuItems}
        />
      </Container>
    </>
  );
};

const App = () => (
  <CustomThemeProvider>
    <StyledThemeProvider theme={useTheme().theme}>
      <AppContent />
    </StyledThemeProvider>
  </CustomThemeProvider>
);

export default App;
