// UserContext.js
import React, { createContext, useState, useEffect } from 'react';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [isCheckingAuth, setIsCheckingAuth] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_URL}/api/users/me`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (response.ok) {
                    const userData = await response.json();
                    setUser(userData);
                } else {
                    console.error('Не удалось получить данные пользователя');
                    setUser(null); // Сброс пользователя без вызова logout
                }
            } catch (error) {
                console.error('Ошибка при получении данных пользователя:', error);
                setUser(null); // Сброс пользователя без вызова logout
            } finally {
                setIsCheckingAuth(false); // Завершение проверки авторизации
            }
        };

        fetchUserData();
    }, []);

    const login = (userData, token) => {
        setUser(userData);
        localStorage.setItem('token', token);
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('token');
        window.location.href = '/'; // Перенаправление только при явном выходе
    };

    return (
        <UserContext.Provider value={{ user, login, logout, isCheckingAuth }}>
            {children}
        </UserContext.Provider>
    );
};
