import React from 'react'

const Login = ({ setActiveComponent, setIsAuthenticated }) => {
    const handleLogin = () => {
        // Логика авторизации
        setIsAuthenticated(true)
    }

    return (
        <div className="container">
            <h2 className="title">Вход</h2>
            <input type="text" placeholder="Логин, почта или телефон" className="input-field" />
            <input type="password" placeholder="Ваш пароль" className="input-field" />
            <label className="checkbox-container">
                <input type="checkbox" /> Запомнить меня
            </label>
            <button className="button button-auth" onClick={handleLogin}>Войти</button>
            <button className="button button-register" onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
            <a href="#" className="forgot-password" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</a>
        </div>
    )
}

export default Login


