import styled from 'styled-components';

import EpicGamesBackgroundImg from '../../assets/EpicGames/images/EpicGamesBackground.png';

const EpicGamesBackgroundImages = styled.div`
    position: 'fixed';
    bottom: 0;
    width: 100%;
    height: 100%;
    background: url(${EpicGamesBackgroundImg}) center/cover no-repeat;
`;

const GradientOverlay = styled.div`
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 35%; // Занимает 35% высоты снизу
    background: linear-gradient(
        to top,
        rgba(30, 37, 56, 1) 0%,   // 100% прозрачность
        rgba(30, 37, 56, 1) 50%,  // 100% прозрачность
        rgba(30, 37, 56, 0.5) 75%,// 50% прозрачность
        rgba(30, 37, 56, 0.25) 88%,// 25% прозрачность
        rgba(30, 37, 56, 0) 100%  // 0% прозрачность
    );
`;

const EpicGamesBackground = () => {
    return (
        <EpicGamesBackgroundImages>
            <GradientOverlay />
        </EpicGamesBackgroundImages>
    )
}

export default EpicGamesBackground;
