// src/components/ui/ChatWindow.js
import styled, { css } from 'styled-components';

export const ChatWindow = styled.div`
  position: fixed;
  ${props => props.isMobile 
    ? 'bottom: 0; right: 0; left: 0; height: 50%;' 
    : 'bottom: 1rem; left: 1rem; width: 320px; height: 400px;'}
  background-color: ${props => props.theme.chat.background};
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0,0,0,0.3);
  z-index: 1000;
  display: ${props => props.isOpen ? 'flex' : 'none'};
  flex-direction: column;
  border: 1px solid ${props => props.theme.border};
`;

export const ChatHeader = styled.div`
  background-color: ${props => props.theme.primary};
  color: white;
  padding: 1rem;
  border-radius: 12px 12px 0 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

export const ChatTabs = styled.div`
  display: flex;
  border-bottom: 1px solid ${props => props.theme.border};
`;

export const ChatTab = styled.button`
  flex: 1;
  padding: 0.75rem;
  text-align: center;
  background: ${props => props.active ? props.theme.surface : 'transparent'};
  color: ${props => props.active ? props.theme.text : props.theme.textSecondary};
  border-bottom: ${props => props.active ? `2px solid ${props.theme.primary}` : 'none'};
  font-weight: 500;
  
  &:hover {
    color: ${props => props.theme.text};
  }
`;

export const ChatMessages = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

export const Message = styled.div`
  display: flex;
  gap: 0.75rem;
  align-items: flex-start;
  
  ${props => props.isOwn && css`
    flex-direction: row-reverse;
  `}
`;

export const MessageAvatar = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  object-fit: cover;
`;

export const MessageContent = styled.div`
  background-color: ${props => props.isOwn ? props.theme.primary : props.theme.chat.message};
  color: ${props => props.isOwn ? 'white' : props.theme.chat.text};
  padding: 0.75rem;
  border-radius: 12px;
  max-width: 80%;
  
  ${props => props.isOwn && css`
    border-bottom-right-radius: 4px;
  `}
  
  ${props => !props.isOwn && css`
    border-bottom-left-radius: 4px;
  `}
`;

export const MessageInfo = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-top: 0.25rem;
  font-size: 0.75rem;
  color: ${props => props.theme.chat.textSecondary};
`;

export const ChatInput = styled.div`
  padding: 1rem;
  border-top: 1px solid ${props => props.theme.border};
  display: flex;
  gap: 0.5rem;
`;

export const ChatInputField = styled.input`
  flex: 1;
  background-color: ${props => props.theme.surface};
  color: ${props => props.theme.text};
  border: 1px solid ${props => props.theme.border};
  border-radius: 20px;
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
`;

export const ChatSendButton = styled.button`
  background-color: ${props => props.theme.primary};
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
`;
