import styled from 'styled-components';
import { Home, Book, Settings, LogOut } from 'lucide-react';

const MobileMenuStyled = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: ${props => props.theme.background};
  z-index: 2000;
  display: ${props => props.isOpen ? 'flex' : 'none'};
  flex-direction: column;
`;

const MobileMenuHeader = styled.div`
  padding: 1rem;
  border-bottom: 1px solid ${props => props.theme.border};
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const MobileMenuNav = styled.nav`
  flex: 1;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  
  a {
    color: ${props => props.theme.text};
    text-decoration: none;
    padding: 1rem;
    border-radius: 8px;
    background-color: ${props => props.theme.surface};
    display: flex;
    align-items: center;
    gap: 0.75rem;
    
    &:hover {
      background-color: ${props => props.theme.card};
    }
  }
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0,0,0,0.5);
  z-index: 1500;
  display: ${props => props.show ? 'block' : 'none'};
`;

// Меню-элементы (временно здесь, в реальности — из пропсов или контекста)
const menuItems = [
  { icon: Home, label: 'Главная', href: '#' },
  { icon: Book, label: 'Игры', href: '#' },
  { icon: Book, label: 'Магазин', href: '#' },
  { icon: Settings, label: 'Настройки', href: '#' },
  { icon: LogOut, label: 'Выйти', href: '#' }
];

const MobileMenu = ({ isOpen, onClose }) => {
  return (
    <>
      <Overlay show={isOpen} onClick={onClose} />
      <MobileMenuStyled isOpen={isOpen}>
        <MobileMenuHeader>
          <h3>Меню</h3>
          <button onClick={onClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor"><path d="M18 6L6 18M6 6l12 12"/></svg>
          </button>
        </MobileMenuHeader>
        <MobileMenuNav>
          {menuItems.map((item, index) => (
            <a key={index} href={item.href}>
              <item.icon size={20} />
              {item.label}
            </a>
          ))}
        </MobileMenuNav>
      </MobileMenuStyled>
    </>
  );
};

export default MobileMenu;
