import './Register.css'
import React, { useState } from 'react'
import axios from 'axios'

const Register = () => {
    const [login, setLogin] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [repeatPassword, setRepeatPassword] = useState('')
    const [displayName, setDisplayName] = useState('')
    const [errors, setErrors] = useState({})
    const [loading, setLoading] = useState(false)
    const [successMessage, setSuccessMessage] = useState('')

    const validateEmail = (email) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
        return re.test(String(email).toLowerCase())
    }

    const validateLogin = (login) => {
        const re = /^[a-zA-Zа-яА-Я0-9@$!%*?&^#-_]{3,50}$/
        return re.test(login)
    }

    const validatePassword = (password) => {
        const re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/
        return re.test(password)
    }

    const validateForm = () => {
        const newErrors = {}
        if (!login) newErrors.login = 'Логин обязателен'
        else if (!validateLogin(login)) newErrors.login = 'Некорректный логин'
        if (!email) newErrors.email = 'Email обязателен'
        else if (!validateEmail(email)) newErrors.email = 'Некорректный email'
        if (!password) newErrors.password = 'Пароль обязателен'
        else if (!validatePassword(password)) newErrors.password = 'Пароль должен содержать минимум 8 символов, включая хотя бы одну заглавную букву, одну строчную букву и одну цифру.'
        if (password !== repeatPassword) newErrors.repeatPassword = 'Пароли не совпадают'
        if (!displayName) newErrors.displayName = 'Имя пользователя обязательно'

        setErrors(newErrors)
        return Object.keys(newErrors).length === 0
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        if (!validateForm()) return

        setLoading(true)
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/register`, { login, email, password, displayName })
            if (response.data && response.data.msg) {
                setSuccessMessage(response.data.msg)
                // Сброс формы
                setLogin('')
                setEmail('')
                setPassword('')
                setRepeatPassword('')
                setDisplayName('')
                setErrors({})
            } else if (response.data && response.data.errors) {
                setErrors({ form: response.data.errors })
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' })
            }
        } catch (err) {
            console.error(err)
            if (err.response && err.response.data && err.response.data.errors) {
                setErrors({ form: err.response.data.errors })
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' })
            }
        } finally {
            setLoading(false)
        }
    }

    return (
        <form className="register-form" onSubmit={handleSubmit}>
            <div>
                <label htmlFor="login">Логин:</label>
                <input
                    type="text"
                    id="login"
                    value={login}
                    onChange={(e) => setLogin(e.target.value)}
                />
                {errors.login && <span className="error">{errors.login}</span>}
            </div>
            <div>
                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                {errors.email && <span className="error">{errors.email}</span>}
            </div>
            <div>
                <label htmlFor="password">Пароль:</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {errors.password && <span className="error">{errors.password}</span>}
            </div>
            <div>
                <label htmlFor="repeatPassword">Повторите пароль:</label>
                <input
                    type="password"
                    id="repeatPassword"
                    value={repeatPassword}
                    onChange={(e) => setRepeatPassword(e.target.value)}
                />
                {errors.repeatPassword && <span className="error">{errors.repeatPassword}</span>}
            </div>
            <div>
                <label htmlFor="displayName">Имя пользователя:</label>
                <input
                    type="text"
                    id="displayName"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                />
                {errors.displayName && <span className="error">{errors.displayName}</span>}
            </div>
            {errors.form && <span className="error">{errors.form}</span>}
            {successMessage && <span className="success">{successMessage}</span>}
            <button type="submit" disabled={loading}>
                {loading ? 'Загрузка...' : 'Зарегистрироваться'}
            </button>
        </form>
    )
}

export default Register
